mapper.update:
update sys_book_storage
set residue_count = residue_count + #{count}
where id = #{id};

update sys_book_storage
set residue_count = residue_count - #{count}
where id = #{id};

update sys_book_storage
set last_add_time = #{lastAddTime}, residue_count = #{residueCount}
where id = #{id};
